import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import './styling.css';

class CodingChallenge extends Component {
  state = {
    firstName: '',
  }

    handleSubmit = (e) => {
      e.preventDefault();
      let updatedName = e.target.value;
      this.setState({ firstName: updatedName });
    }

